import React, { Component } from 'react';
import io from 'socket.io-client';
import { Card } from 'react-bootstrap';

const socket = io();

export class Chat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      messages: [],
      currentMessage: ''
    };
  }

  setMessage = text => {
    this.setState({ currentMessage: text });
  };

  sendMsg = e => {
    e.preventDefault();
    socket.emit('chat-message', document.getElementById('m').value);
  };

  render() {
    return (
      <div>
        <Card>
          <div className='chat-display'>
            <ul></ul>
          </div>

          <div className='input'>
            <input type='text' name='' onChange={this.setMessage} />
            <button>Send</button>
          </div>
        </Card>
      </div>
    );
  }
}

export default Chat;
