//Cart page Component

//Module import
import React, { Component } from 'react';

//Style import
import './Cart.css';

class Cart extends Component {
  constructor(props) {
    super(props);

    this.state = {};
  }

  displayCart = () => {
    const cart = this.props.cart.map((element, index) => (
      <ul key={index}>
        <li>{element.pizza}</li>
        <li>{element.price}</li>
      </ul>
    ));
    return cart;
  };

  totalPrice = () => {
    return 10 * this.props.cart.length;
  };
  render() {
    return (
      <div>
        {this.displayCart()}
        <p>{this.totalPrice}</p>
      </div>
    );
  }
}

export default Cart;
