import React, { Component } from 'react';
import { Container } from 'react-bootstrap';
import { Row } from 'react-bootstrap';
import { Col } from 'react-bootstrap';
import { Card } from 'react-bootstrap';
import { menu } from '../../data/menu.json';
import Table from 'react-bootstrap/Table';

import './Menu.css';

export default class Menu extends Component {
	menu = () => {
		const pizzas = menu.map((element, index) => (
			<li key={index} className="pizza">
				<span className="pizzaName"> {element.pizza} </span>
				{element.description}
				<span className="pizzaPrice"> {element.price} </span>
			</li>
		));
		return pizzas;
	};

	render() {
		return (
			<div id="menu">
				<div className="content">
					<h1 className="main-heading">Notre Selection de Pizzas</h1>
					<Container>
						<Row noGutters="true">
							<Col md={6}>
								<Card>
									<Table
										striped
										bordered
										hover
										variant="dark"
									>
										<thead>
											<tr>
												<th>#</th>
												<th>First Name</th>
												<th>Last Name</th>
												<th>Username</th>
											</tr>
										</thead>
										{this.menu()}
									</Table>
								</Card>
							</Col>
						</Row>
					</Container>
				</div>

				<div className="overlay"></div>
			</div>
		);
	}
}
