import React, { Component } from 'react';
import io from 'socket.io-client';
import { Card } from 'react-bootstrap';

export class Chat extends Component {
  constructor(props) {
    super(props);
    this.state = {
      messages: []
    };
  }

  sendMsg = e => {
    e.preventDefault();
    io.emit('chat-message', document.getElementById('m').value);
  };

  render() {
    return (
      <div>
        <Card>
          <div className='chat-display'>
            <ul></ul>
          </div>

          <div className='input'>
            <form action='' onSubmit={this.sendMsg}>
              <input type='text' name='' id='m' />
              <button>Send</button>
            </form>
          </div>
        </Card>
      </div>
    );
  }
}

export default Chat;
