import React, { Component } from 'react';
import { toppings } from '../../data/toppings.json';
import { Container } from 'react-bootstrap';
import { Row } from 'react-bootstrap';
import { Col } from 'react-bootstrap';
import { Card } from 'react-bootstrap';
import Draggable from './Dnd/Draggable/index';
import Dropable from './Dnd/Dropable/index';

import './PizzaPerso.css';

export class PizzaPerso extends Component {
  createPizza = () => {
    const ingredients = toppings.map((element, index) => (
      <Draggable id={index}>
        <p>{element.name}</p>
      </Draggable>
    ));
    return ingredients;
  };

  render() {
    return (
      <div className='toppings-container'>
        <Container>
          <Row>
            <Col>
              <Dropable id='dr1'>
                <Card>
                  <h2 className='tc'>Les Toppings</h2>
                  <h4 className='tc'>1$ par topping</h4>
                  {this.createPizza()}
                </Card>
              </Dropable>
            </Col>
            <Col>
              <Dropable id='dr2'>
                <Card>
                  <h2 className='tc'>Votre Pizza</h2>
                  <ul></ul>
                </Card>
              </Dropable>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default PizzaPerso;
